<template>
  <div>
    <b-container fluid class="no-margins">
      <b-row no-gutters>
        <b-col>
          <b-form inline>
            <b-button :disabled="pageNum == 1" @click="previousPage"
              >Prev</b-button
            >
            <span>
              <b-form-input
                style="width: 50px"
                size="sm"
                v-model="pageNum"
              ></b-form-input
              >/ {{ nbPages }}</span
            >
            <b-button :disableD="pageNum == nbPages" @click="nextPage"
              >Next</b-button
            >
          </b-form>
        </b-col>
      </b-row>
      <b-row no-gutters>
        <b-col id="pdf-layer-parent" class="scrollable-xy">
          <canvas id="pdf-layer" @wheel="zoomEvent"></canvas>
        </b-col>
      </b-row>
    </b-container>
  </div>
</template>

<script>
import * as pdfjs from "pdfjs-dist/webpack";

export default {
  name: "PdfReader",
  props: {},
  components: {},
  computed: {},

  data: () => ({
    pdf: null,
    page: null,
    nbPages: 0,
    canvas: null,
    context: null,
    scale: 1,
    pageNum: 1,
    ro: null,
  }),
  watch: {
    async pageNum(newPageNum) {
      if (newPageNum > this.nbPages) this.pageNum = this.nbPages;
      await this.loadPage(Number(newPageNum));
    },
  },
  methods: {
    async loadPDF() {
      let loadingTask = pdfjs.getDocument(
        "data:application/pdf;base64,"
      );
      this.pdf = await loadingTask.promise;
      this.nbPages = this.pdf.numPages;
      this.canvas = document.getElementById("pdf-layer");
      this.context = this.canvas.getContext("2d");
    },

    async nextPage() {
      if (this.pageNum == this.nbPages) return;
      this.pageNum++;
      await this.loadPage(this.pageNum);
    },
    async previousPage() {
      if (this.pageNum == 1) return;
      this.pageNum--;
      await this.loadPage(this.pageNum);
    },
    zoomEvent(e) {
      console.log(e);
      if (e.ctrlKey) {
        if (e.deltaY < 0) {
          this.scale *= 1.5;
        } else {
          this.scale /= 1.5;
        }
        this.setPageScale();
      }
    },

    async setPageScale() {
      console.log("asfdafse");
      let viewport = this.page.getViewport({ scale: this.scale });
      this.canvas.width = viewport.width;
      this.canvas.height = viewport.height;
      this.page.render({
        canvasContext: this.context,
        viewport: viewport,
      });
    },

    async loadPage(pageNb) {
      this.scale = 1;
      this.page = await this.pdf.getPage(pageNb);
      let viewport = this.page.getViewport({ scale: this.scale });
      this.canvas.width = viewport.width;
      this.canvas.height = viewport.height;
      this.page.render({
        canvasContext: this.context,
        viewport: viewport,
      });
    },
  },
  async mounted() {
    await this.loadPDF();
    await this.loadPage(1);
  },
};
</script>

<style>
.toc-element {
  margin: 2px;
  overflow-x: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.scrollable-xy {
  overflow: scroll;
}

.no-margins {
  margin: 0;
  padding: 0;
}
</style>